import Layout from "../Layout/Layout";
import UserPost from "../UserPost/UserPost";

import axios, {post} from "axios";
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";

const post1 = {
    "handle" : "@enki",
    "username" : "EnkiTheMystik",
    "date" : new Date().toDateString(),
    "content" : "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
    "comments" : 1,
    "likes" : 0
}

const post2 = {
    "handle" : "@johndoe",
    "username" : "John Doe",
    "date" : new Date().toDateString(),
    "content" : "This app kind of blows tho?",
    "comments" : 0,
    "likes" : 347
}

const Home = () => {
    const [posts, setPosts] = useState([]);
    const url = useParams();

    function  getUserPosts() {
        axios.get('/getUserPosts').then((response) => {
            let data = response.data;
            setPosts(data);
            console.log(response.data);
        })
    }

    useEffect(() => {
        getUserPosts();
    }, []); // The empty array tells React to only run the effect once (when the component mounts)

    return (
        //Checks if posts is empty, the maps through the array of UserPosts and renders them all
        <Layout>
            {posts.length > 0 ? (
                posts.map((post) => {
                    return (
                        <UserPost
                            handle={post.user_handle}
                            username={post.user_handle}
                            date={post.post_date}
                            content={post.post_content}
                            comments={post.comment_count}
                            likes={post.likes}
                        />
                    );
                })
            ) : (
                <div>Loading...</div>
            )}

        </Layout>

    )
}

export default Home;
